import './App.css';
import IVimg from './iv.jpeg';
import React from 'react';
import {useState} from "react";
import Axios from "axios";

function App() {
  const [room, setRoom] = useState(0);
  const [bathroom, setBathroom] = useState(0);
  const [building, setBuilding] = useState(0);
  const [location, setLocation] = useState(0);
  const [description, setDescription] = useState(0);
  const [ratings, setRatings] = useState([]);
  const [avgRating, setAvgRating] = useState(0);
  const [numReviews, setNumReviews] = useState(0);

  const setInput = (e, inputType) => {
    if (inputType === "room") {
      setRoom(e.target.value);
    }
    if (inputType === "bathroom") {
      setBathroom(e.target.value);
    }
    if (inputType === "building") {
      setBuilding(e.target.value);
    }    
    if (inputType === "location") {
      setLocation(e.target.value);
    }
    if (inputType === "description") {
      setDescription(e.target.value);
    }
  }

  const addRating = () => {
    Axios.post("http://localhost:3001/create", "http://localhost:3001/avgRating", {
      room: room,
      bathroom: bathroom, 
      building: building, 
      location: location, 
      description: description,
      avgRating: avgRating
    }).then(() => {
      setRatings([
        {
          room: room,
          bathroom: bathroom, 
          building: building, 
          location: location, 
          description: description
        },
        ...ratings
      ]);
      setAvgRating(avgRating);
      setNumReviews();
    });
  };

  return (
    <div className="App">
      <h1>RateMyDorms</h1>
      <div className="content">
        <div className="information">
          <h2>Rating</h2>
          <div className="rating">
            <label>Room</label>
            <input 
              type="number" 
              onChange={(event) => {
                setInput(event, "room");
              }}
            />
          </div>
          <div className="rating">
            <label>Bathroom</label>
            <input 
              type="number" 
              onChange={(event) => {
                setInput(event, "bathroom");
              }}
            />
          </div>
          <div className="rating">
            <label>Building</label>
            <input 
              type="number"
              onChange={(event) => {
                setInput(event, "building");
              }} 
            />
          </div>
          <div className="rating">
            <label>Location</label>
            <input 
              type="number"
              onChange={(event) => {
                setInput(event, "location");
              }}
            />
          </div>
          <div className="rating">
            <textarea 
              type="text"
              onChange={(event) => {
                setInput(event, "description");
              }}
            />
          </div>
          <button onClick={addRating}>Submit</button>
        </div>
        <div className="ratings">
          <h2>International Village</h2>
          <img src={IVimg} alt="International Village"></img>
          <p>1155 Tremont St, Boston, MA 02120</p>
          <h3>Northeastern University</h3>
          <div className='school-overview'>
            <div className='overview-section'>
              <div id="avgRating">
                <h2>{avgRating}</h2>
                <h3>Average Rating</h3>
              </div>
            </div>
            <div className='overview-section'>
              <div id="numReviews">
                <h2>{numReviews}</h2>
                <h3>Number of Reviews</h3>
              </div>
            </div>
          </div>
          {ratings.map((val, key) => {
            return (
              <div id='ratingsList'> 
                <div id='ratingsNums'>
                  <h3 id='ratingVal'>Room: {val.bathroom}</h3> 
                  <h3 id='ratingVal'>Bathroom: {val.bathroom}</h3> 
                  <h3 id='ratingVal'>Building: {val.building}</h3> 
                  <h3 id='ratingVal'>Location: {val.location}</h3> 
                </div>
                <p id='ratingDesc'>{val.description}</p> 
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
